<script>
  $(document).ready(function() {
    const value = {
      radiobox1: 1,
      radiobox2: 1,
      checkbox: 0,
      slider: 800,
      spinner: 800,
    };
    $(".slider").slider({
      min: 0,
      max: 3000,
      range: "min",
      slide: function(event, ui) {
        $(".spinner").spinner("value", ui.value);
        value.slider = ui.value;
        setValue();
      },
    });
    $(".spinner").spinner({
      min: 0,
      max: 3000,
      step: 1,
      spin: function(event, ui) {
        $(".slider").slider("value", ui.value);
        value.slider = ui.value;
        setValue();
      },

    });

    $(".radiobox1").click(function() {

      if (!$(this).hasClass("checked")) {
        $(".radiobox1").removeClass("checked");
        $(this).addClass("checked");
        input = $(this).find("input");
        input.prop("checked", true);
        value.radiobox1 = input.data("value");
      }
      setValue();
    });
    $(".radiobox2").click(function() {

      if (!$(this).hasClass("checked")) {
        $(".radiobox2").removeClass("checked");
        $(this).addClass("checked");
        input = $(this).find("input");
        input.prop("checked", true);
        value.radiobox2 = input.data("value");
      }
      setValue();
    });

    $(".checkbox").find("input").change(function() {
      input = $(this);
      if (!input.prop("checked")) {
        value.checkbox -= Number(input.data("value"));
      } else {
        value.checkbox += Number(input.data("value"));
      }
      setValue();
    });
    start();

    function start() {
      $(".spinner").spinner("value", 800);
      $(".slider").slider("value", 800);
      setValue();
    }

    function setValue() {
      let data = 0;
      if (value.slider) {
        data = Math.round(value.slider / 5 *
          (value.radiobox1 ? value.radiobox1 / 5 : 1) *
          (value.radiobox2 ? value.radiobox2 / 5 : 1) *
          (value.checkbox > 1 ? value.checkbox / 5 : 1));

      }
      $(".value").text(data);
    }
  });
</script>

